import React ,{ Component} from 'react'
import DenseTablePapers from './components/DenseTablePapers'
import TitleFilterPaper from './components/titleFilter'
import CriterioFitler from './components/criterioFilter'
import Paper from './model/Paper'

class app extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      papers :[],
      title :''
    }; 

    this.changeTitle = this.changeTitle.bind(this);
    this.searchPaper = this.searchPaper.bind(this);
  }

  componentDidMount() {
    let urlPapers ='https://tesisservice.azurewebsites.net/api/Papers/GetPapers?title=' + this.state.title;
    fetch(urlPapers)
      .then((response) => response.json())
      .then((data) => {
        this.setState({papers : data.list}) 
      });
  }

  changeTitle(newTitle){
    this.setState({title : newTitle});
  }

  searchPaper(){

    let urlPapers ='https://tesisservice.azurewebsites.net/api/Papers/GetPapers?title=' + this.state.title;

    fetch(urlPapers)
      .then((response) => response.json())
      .then((data) => {
        this.setState({papers : data.list}) 
      });
  }

  render(){
    return (
    <div>
      <CriterioFitler></CriterioFitler>
      <TitleFilterPaper paper={this.state.paper} 
                changeTitle={this.changeTitle}
                searchPaper={this.searchPaper}></TitleFilterPaper>    
       <DenseTablePapers dataPapers={this.state.papers}></DenseTablePapers>
    </div>
    )

  }
}

export default app;
